mapper.select:
SELECT ScreenID, ScreenCD, ScreenNm, ScreenSeat, TheaterID
FROM tbl_screen
WHERE TheaterID=#{TheaterID}

SELECT ScreenID, ScreenCD, ScreenNm, ScreenSeat, TheaterID
FROM tbl_screen
WHERE ScreenID=#{ScreenID}

mapper.update:
UPDATE tbl_screen
SET ScreenNm=#{ScreenNm}, ScreenSeat=#{ScreenSeat}, TheaterID=#{TheaterID}
WHERE ScreenID=#{ScreenID}
