mapper.select:
SELECT SeatID, SeatNum, SeatState, ScreenCD
FROM tbl_seat
WHERE ScreenCD=#{ScreenCD}

SELECT SeatID, SeatNum, SeatState, ScreenCD
FROM tbl_seat
WHERE SeatID=#{SeatID}

mapper.update:
UPDATE tbl_seat
SET SeatNum=#{SeatNum}, SeatState=#{SeatState}, ScreenCD=#{ScreenCD}
WHERE SeatID=#{SeatID}
