mapper.select:
SELECT TheaterID, TheaterNm, Address, Tell, LocationID
FROM tbl_theater
WHERE LocationID=#{LocationID}

SELECT TheaterID, TheaterNm, Address, Tell, LocationID
FROM tbl_theater
WHERE TheaterID=#{TheaterID}

mapper.update:
UPDATE tbl_theater
SET TheaterNm=#{TheaterNm}, Address=#{Address}, Tell=#{Tell}
WHERE TheaterID=#{TheaterID}
